    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title 'Se connecter' %}

{% block body %}
    <div class=\"container mb-5\" style=\"width: 500px; margin-top: 100px; margin-left: 500px;\">

        <form action=\"{{ path('login') }}\" method=\"post\">

            {% if error %}
                <div class=\"alert alert-danger\">
                    <p>{{ error.messageKey | trans(error.messagedata, 'security') }}</p>
                </div>
            {% endif %}
            {% if app.user %}
                <div class=\"mb-3\">
                    You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
                </div>
            {% endif %}

            <div class=\"form-group\">
                <label for=\"username\">Nom d'utilisateur</label>
                <input type=\"text\" id=\"username\" name=\"_username\" class=\"form-control\" value=\"{{ last_username }}\">
            </div>
            <div class=\"form-group\">
                <label for=\"username\">Mot de passe</label>
                <input type=\"password\" id=\"password\" name=\"_password\" class=\"form-control\">
                <input type=\"hidden\" name=\"_csrf_token\"
                       value=\"{{ csrf_token('authenticate') }}\"
                >
            </div>
            <button type=\"submit\" class=\"btn btn-dark p-3\">Se connecter</button>
            <button type=\"submit\" class=\"btn btn-secondary\"><a class=\"nav-link text-light\" href=\"{{ path('app_register') }}\">S'enregistrer</a></button>

        </form>
    </div>
{% endblock %}", "security/login.html.twig", "/Users/home/FEELUNIQUE/templates/security/login.html.twig");
